import { Link } from "react-router-dom";
import { Menu } from "antd";
import { Api } from "api"

interface MenuType {
  categories: {
    id: number;
    type: string;
    label: string;
  }[];
}
 Api.getMenu() 
export const MenuSide = (props: MenuType) => {
  return (
    <div>
      <Menu mode="vertical">
        {props.categories.map((item) => (
          <Menu.Item key={item.id}>
            <Link to={`/${item.type}`}> {item.label} </Link>
          </Menu.Item>
        ))}
      </Menu>
    </div>
  );
};
